import {
  patchState,
  signalStore,
  withComputed,
  withHooks,
  withMethods,
  withState,
} from '@ngrx/signals';
import { computed, inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Employee } from '../Employee';
import { lastValueFrom } from 'rxjs';

// Define the API response interface to match JSONPlaceholder structure
interface ApiUser {
  id: number;
  name: string;
  username: string;
  email: string;
  address: {
    street: string;
    suite: string;
    city: string;
    zipcode: string;
    geo: {
      lat: string;
      lng: string;
    };
  };
  phone: string;
  website: string;
  company: {
    name: string;
    catchPhrase: string;
    bs: string;
  };
}

type EmployeeState = {
  initialized: boolean;
  error: string | null;
  employees: Employee[];
};

const initialState: EmployeeState = {
  initialized: false,
  error: null,
  employees: [],
};

const apiUrl = 'https://jsonplaceholder.typicode.com/users';

export const EmployeeStore = signalStore(
  { providedIn: 'root' },
  withState(initialState),
  withComputed(({ employees }) => ({
    employees: computed(() => employees()),
    isLoading: computed(() => !initialState.initialized),
  })),
  withMethods((store, http = inject(HttpClient)) => ({
    async loadEmployees() {
      try {
        // Check localStorage first
        const localData = localStorage.getItem('employees');
        if (localData) {
          const employees: Employee[] = JSON.parse(localData);
          patchState(store, { employees, initialized: true, error: null });
          return;
        }

        // Fetch from API if not in localStorage
        const response = await lastValueFrom(http.get<ApiUser[]>(apiUrl));
        const mappedEmployees: Employee[] = response.map(user => ({
          id: user.id,
          name: user.name,
          company: user.company.name,
          bs: user.company.bs,
          website: user.website,
        }));
        localStorage.setItem('employees', JSON.stringify(mappedEmployees));
        patchState(store, { employees: mappedEmployees, initialized: true, error: null });
      } catch (error) {
        patchState(store, { error: 'Failed to load employees' });
        throw error;
      }
    },
    addEmployee(employee: Employee) {
      try {
        const newEmployees = [...store.employees(), { ...employee, id: Date.now() }]; // Use timestamp for unique ID
        localStorage.setItem('employees', JSON.stringify(newEmployees));
        patchState(store, { employees: newEmployees, error: null });
      } catch (error) {
        patchState(store, { error: 'Failed to add employee' });
        throw error;
      }
    },
    updateEmployee(employee: Employee) {
      try {
        const updatedEmployees = store.employees().map(emp =>
          emp.id === employee.id ? employee : emp
        );
        localStorage.setItem('employees', JSON.stringify(updatedEmployees));
        patchState(store, { employees: updatedEmployees, error: null });
      } catch (error) {
        patchState(store, { error: 'Failed to update employee' });
        throw error;
      }
    },
    deleteEmployee(empId: number) {
      try {
        const updatedEmployees = store.employees().filter(emp => emp.id !== empId);
        localStorage.setItem('employees', JSON.stringify(updatedEmployees));
        patchState(store, { employees: updatedEmployees, error: null });
      } catch (error) {
        patchState(store, { error: 'Failed to delete employee' });
        throw error;
      }
    },
    getEmployee(empId: number): Employee | undefined {
      try {
        return store.employees().find(emp => emp.id === empId);
      } catch (error) {
        patchState(store, { error: `Failed to fetch employee with ID ${empId}` });
        throw error;
      }
    },
  })),
  withHooks({
    onInit(store) {
      store.loadEmployees();
    },
  })
);

export interface Employee {
    id: number;
    name: string;
    company: string;
    bs: string;
    website: string;
  }

import { Component, computed, Input, signal, inject } from '@angular/core';
import { MatListModule } from '@angular/material/list';
import { MatIconModule } from '@angular/material/icon';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { DarkModeService } from 'src/app/services/dark-theme/dark-mode.service';
import { ResponsiveService } from 'src/app/services/responsive/responsive.service';
import { ThemeService } from 'src/app/services/theme/theme.service'; 

interface MenuItem {
  icon: string;
  label: string;
  route: string;
}

@Component({
  selector: 'app-custom-sidenav',
  standalone: true,
  imports: [CommonModule, MatListModule, MatIconModule, RouterModule],
  templateUrl: './custom-sidenav.component.html',
  styleUrl: './custom-sidenav.component.scss'
})
export class CustomSidenavComponent {
  public responsiveService = inject(ResponsiveService);
  public darkModeService = inject(DarkModeService);
  public themeService = inject(ThemeService);

  menuItems = signal<MenuItem[]>([
    { icon: 'dashboard', label: 'Dashboard', route: 'dashboard' },
    { icon: 'assignment', label: 'Form', route: 'form' },
    { icon: 'analytics', label: 'Analytics', route: 'analytics' },
    { icon: 'comment', label: 'Comments', route: 'comments' },
    { icon: 'store', label: 'Store', route: 'store' },
    { icon: 'group', label: 'Employee', route: 'employee' }
  ]);

  sideNavCollapsed = signal(false);
  @Input() set collapsed(val: boolean) {
    this.sideNavCollapsed.set(val);
  }

  // Access the current theme
  currentTheme = computed(() => this.themeService.currentTheme());
}

import { Injectable, signal, effect } from '@angular/core';
import { DarkModeService } from '../dark-theme/dark-mode.service';

export interface Theme {
  id: string;
  primary: string;
  displayName: string;
  darkModeClass: string;
  lightModeClass: string;
}

@Injectable({
  providedIn: 'root',
})
export class ThemeService {
  private readonly themes: Theme[] = [
    {
      id: 'deep-blue',
      primary: '#1976D2',
      displayName: 'Deep-Blue',
      darkModeClass: 'deep-blue-dark-theme',
      lightModeClass: 'deep-blue-light-theme'
    },
    { 
      id: 'green', 
      primary: '#00796B', 
      displayName: 'Green',
      darkModeClass: 'green-dark-theme',
      lightModeClass: 'green-light-theme'
    },
    { 
      id: 'orange', 
      primary: '#E65100', 
      displayName: 'Orange',
      darkModeClass: 'orange-dark-theme',
      lightModeClass: 'orange-light-theme'
    },
    { 
      id: 'purple', 
      primary: '#6200EE', 
      displayName: 'Purple',
      darkModeClass: 'purple-dark-theme',
      lightModeClass: 'purple-light-theme'
    },
    { 
      id: 'red', 
      primary: '#C2185B', 
      displayName: 'Red',
      darkModeClass: 'red-dark-theme',
      lightModeClass: 'red-light-theme'
    },
  ];

  currentTheme = signal<Theme>(this.themes[0]);

  constructor(private darkModeService: DarkModeService) {
    // Set up an effect to apply theme whenever dark mode changes
    effect(() => {
      this.applyCurrentTheme();
    });
  }

  getThemes(): Theme[] {
    return this.themes;
  }

  setTheme(themeId: string): void {
    const theme = this.themes.find((t) => t.id === themeId);
    if (theme) {
      this.currentTheme.set(theme);
      this.applyCurrentTheme();
    }
  }

  private applyCurrentTheme(): void {
    // Remove all theme classes first
    document.body.classList.remove(
      ...this.themes.flatMap(t => [t.darkModeClass, t.lightModeClass])
    );

    // Add the appropriate theme class based on dark mode
    const currentTheme = this.currentTheme();
    const isDarkMode = this.darkModeService.darkMode();
    const themeClass = isDarkMode ? currentTheme.darkModeClass : currentTheme.lightModeClass;

    document.body.classList.add(themeClass);

    // Set CSS variables for icons and other theme properties
    document.body.style.setProperty('--mat-icon-color', 'var(--mat-sys-on-surface)');
    document.body.style.setProperty('--mat-sys-primary', currentTheme.primary);
  }
}

