    // Success message
    .success-message {
      background-color: #1e3a8a;
      color: white;

      mat-icon {
        color: white;
      }
    }

    // Stepper styling
    mat-horizontal-stepper {
      background: transparent;

      .mat-step-header {
        .mat-step-label {
