        }
      }
  }

  // Success message
  .success-message {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 12px 16px;
    margin-bottom: 24px;
    background-color: #e3f2fd;
    color: #0d47a1;
    border-radius: 8px;
    font-size: 14px;

    mat-icon {
      color: #0d47a1;
    }
  }

  // Stepper
  mat-horizontal-stepper {
    background: transparent;

    .mat-step-header {
      .mat-step-label {
        font-weight: 500;
        color: rgba(0, 0, 0, 0.87);
      }

      .mat-step-icon {
        background-color: #3f51b5;
        color: white;
      }
