    }
  }

  // Form grid
  .form-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 16px;

    @media (max-width: 600px) {
      grid-template-columns: 1fr;
    }
  }

  // Age Group Section (checkbox list)
  .age-group-section {
    grid-column: 1 / -1;
    margin-bottom: 16px;

    h3 {
      font-weight: 500;
      margin-bottom: 12px;
