        .mdc-checkbox {
          margin-right: 8px;
        }
      }
    }

    mat-error {
      margin-top: 8px;
      font-size: 12px;
      color: #f44336;
    }
  }



  // Buttons
  .stepper-actions {
    display: flex;
    justify-content: space-between;
    margin-top: 20px;

    button {
      min-width: 120px;
      font-weight: 500;
      letter-spacing: 0.5px;

      &[color="primary"] {
        &:hover {
          transform: translateY(-1px);
        }
      }
    }
  }

  // Review section
  .review-container {
    .review-section {
      margin-bottom: 24px;

