signalStore(
  { providedIn: 'root' },
  withState(initialState),
  withComputed(({ employees }) => ({
    employees: computed(() => employees()),
    isLoading: computed(() => !initialState.initialized),
  })),
  withMethods((store, http = inject(HttpClient)) => ({
    async loadEmployees() {
      try {
        // Check localStorage first
        const localData = localStorage.getItem('employees');
        if (localData) {
          const employees: Employee[] = JSON.parse(localData);
          patchState(store, { employees, initialized: true, error: null });
          return;
        }

        // Fetch from API if not in localStorage
        const response = await lastValueFrom(http.get<ApiUser[]>(apiUrl));
        const mappedEmployees: Employee[] = response.map(user => ({
          id: user.id,
          name: user.name,
          company: user.company.name,
          bs: user.company.bs,
          website: user.website,
        }));
        localStorage.setItem('employees', JSON.stringify(mappedEmployees));
        patchState(store, { employees: mappedEmployees, initialized: true, error: null });
      } catch (error) {
        patchState(store, { error: 'Failed to load employees' });
        throw error;
      }
    },
    addEmployee(employee: Employee) {
      try {
        const newEmployees = [...store.employees(), { ...employee, id: Date.now() }]; // Use timestamp for unique ID
        localStorage.setItem('employees', JSON.stringify(newEmployees));
        patchState(store, { employees: newEmployees, error: null });
      } catch (error) {
        patchState(store, { error: 'Failed to add employee' });
        throw error;
      }
    },
    updateEmployee(employee: Employee) {
      try {
        const updatedEmployees = store.employees().map(emp =>
          emp.id === employee.id ? employee : emp
        );
        localStorage.setItem('employees', JSON.stringify(updatedEmployees));
        patchState(store, { employees: updatedEmployees, error: null });
      } catch (error) {
        patchState(store, { error: 'Failed to update employee' });
        throw error;
      }
    },
    deleteEmployee(empId: number) {
      try {
        const updatedEmployees = store.employees().filter(emp => emp.id !== empId);
        localStorage.setItem('employees', JSON.stringify(updatedEmployees));
        patchState(store, { employees: updatedEmployees, error: null });
      } catch (error) {
        patchState(store, { error: 'Failed to delete employee' });
        throw error;
      }
    },
    getEmployee(empId: number): Employee | undefined {
      try {
        return store.employees().find(emp => emp.id === empId);
      } catch (error) {
        patchState(store, { error: `Failed to fetch employee with ID ${empId}` });
        throw error;
      }
    },
  })),
  withHooks({
    onInit(store) {
      store.loadEmployees();
    },
  })
)

signalStore(
  { providedIn: 'root' },
  withState(initialState),
  withProps((store) => {
    return {
      _todoResource: resource<TodoItem[], string>({
        request: store.searchTerm,
        loader: async (params) => {
          const { request: searchTerm, abortSignal } = params;
          try {
            const resp = await fetch(
              'https://jsonplaceholder.typicode.com/users/1/todos',
              {
                signal: abortSignal,
              }
            );
            if (resp.status !== 200) {
              throw new Error(resp.status.toString());
            }
            const todos = (await resp.json()) as TodoItem[];
            patchState(store, {
              intialized: true,
            });
            return todos.filter((todo) =>
              todo.title.toLowerCase().includes(searchTerm.toLowerCase())
            );
          } catch (error) {
            const todosFromStorage = JSON.parse(
              localStorage.getItem(todoStoreKey)
            ) as TodoItem[];
            return todosFromStorage;
          }
        }
      })
    };
  }),
  withComputed(({ filter, _todoResource }) => ({
    todos: computed(() => _todoResource.value() || []),
    todosLoading: computed(() => _todoResource.isLoading()),
    todosLoadingError: computed(() => _todoResource.error()),
    completedTodos: computed(() =>
      _todoResource.value()?.filter((todoItem) => {
        return todoItem.completed;
      }) || []
    ),
    filteredTodos: computed(() => {
      switch (filter()) {
        case 'completed':
          return _todoResource.value()?.filter((todoItem) => {
            return todoItem.completed;
          }) || [];
        case 'active':
          return _todoResource.value()?.filter((todoItem) => {
            return !todoItem.completed;
          }) || [];
        default:
          return _todoResource.value() || [];
      }
    }),
  })),
  withMethods((store, http = inject(HttpClient)) => ({
    updateSearchTerm(searchTerm: string) {
      patchState(store, {
        searchTerm: searchTerm,
      });
    },
    addTodo(newTodoTitle: string) {
      http
        .post<TodoItem>('https://jsonplaceholder.typicode.com/users/1/todos', {
          title: newTodoTitle,
          completed: false,

        })
        .subscribe((todo) => {
          store._todoResource.update((todos) => {
            if (!todos) {
              return [todo];            
            }
            return [todo, ...todos];
          });
        });
    },
    changeFilter(filter: TodoFilter) {
      console.log({ filter });
      patchState(store, {
        filter,
      });
    },
    toggleTodo(todoId: string) {
      const newCompleted = !store._todoResource.value()?.find((todo) => todo.id === todoId)
        ?.completed;
      http
        .patch<TodoItem>(
          `https://jsonplaceholder.typicode.com/todos/${todoId}`,
          {
            completed: newCompleted,
          }
        )
        .subscribe(() => {
          store._todoResource.update((todos) => {
            if (!todos) {
              return [];
            }
            return todos.map((todoItem) => {
              if (todoItem.id === todoId) {
                return {
                  ...todoItem,
                  completed: newCompleted
                };
              }
              return todoItem;
            });
          });
        });
    },
  })),
  withHooks({
    onInit(store) {
      effect(() => {
        const state = getState(store);
        console.log('effect: ', state);
        if (state.intialized) {
          localStorage.setItem(todoStoreKey, JSON.stringify(store.todos()));
        }
      });
    },
  })
)

